import get from './requests/get'

module.exports = {
    removeToken: function(cb) {
        // create fetch promise

        let headers = {'Authorization': 'Token ' + localStorage.token};
        let url = 'logout/';

        delete localStorage.token;
        get.get(url, headers, (res) => {
            console.log(res);
            if (!res.result.detail.error){
                cb({logged_out: true});

            }
        });
    },
};
