import React from 'react'
import { Link } from 'react-router-dom'
import { useHistory } from 'react-router-dom'
import PropTypes from 'prop-types'
import {
  CButton,
  CCard,
  CCardBody,
  CCardGroup,
  CCol,
  CContainer,
  CForm,
  CInput,
  CInputGroup,
  CInputGroupPrepend,
  CInputGroupText,
  CRow
} from '@coreui/react'
import CIcon from '@coreui/icons-react'






import auth from '../../../utils/auth.js'

class LoginComponent extends React.Component {

    constructor(props, context){
        super(props, context);
        this.state = {
            username: 'your username',
            password: 'your pass'
        };
    }

    handleSubmit = (e) => {
        e.preventDefault();

        let username = this.state.username;
        let pass = this.state.password;
        let history = useHistory();

        auth.login(username, pass, (loggedIn) => {
            if (loggedIn) {
                // history.push("/");

                // history.push({
                // pathname: '/secondpage',
                // search: '?query=abc',
                // state: { detail: 'some_value' }
                // });

            }
            else {
                this.setState({error: 'Incorrect login or password'});
            }
        })
    };

    handleChange = (e) => {
        this.setState({[e.target.name]: e.target.value});
    };

    render() {
      return (
        <div className="c-app c-default-layout flex-row align-items-center">
          <CContainer>
            <CRow className="justify-content-center">
              <CCol md="8">
                <CCardGroup>
                  <CCard className="p-4">
                    <CCardBody>
                      <CForm>
                        <h1>Login</h1>
                        <p className="text-muted">Sign In to your account</p>
                        <CInputGroup className="mb-3">
                          <CInputGroupPrepend>
                            <CInputGroupText>
                              <CIcon name="cil-user" />
                            </CInputGroupText>
                          </CInputGroupPrepend>
                          <CInput type="text" placeholder={ this.state.username } name="username" autoComplete="username" onChange={this.handleChange} />
                        </CInputGroup>
                        <CInputGroup className="mb-4">
                          <CInputGroupPrepend>
                            <CInputGroupText>
                              <CIcon name="cil-lock-locked" />
                            </CInputGroupText>
                          </CInputGroupPrepend>
                          <CInput type="password" placeholder={ this.state.password } name="password" autoComplete="current-password" onChange={this.handleChange}/>
                          <div id="form_error">{ this.state.error }</div>
                        </CInputGroup>
                        <CRow>
                          <CCol xs="6">
                            <CButton onClick={this.handleSubmit} color="primary" className="px-4">Login</CButton>
                          </CCol>
                          <CCol xs="6" className="text-right">
                            <CButton color="link" className="px-0">Forgot password?</CButton>
                          </CCol>
                        </CRow>
                      </CForm>
                    </CCardBody>
                  </CCard>
                  <CCard className="text-white bg-primary py-5 d-md-down-none" style={{ width: '44%' }}>
                    <CCardBody className="text-center">
                      <div>
                        <h2>Sign up</h2>
                        <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut
                          labore et dolore magna aliqua.</p>
                        <Link to="/register">
                          <CButton color="primary" className="mt-3" active tabIndex={-1}>Register Now!</CButton>
                        </Link>
                      </div>
                    </CCardBody>
                  </CCard>
                </CCardGroup>
              </CCol>
            </CRow>
          </CContainer>
        </div>
      )
    }
}

LoginComponent.contextTypes = {
  router: PropTypes.object.isRequired
};

export default LoginComponent
